import os
from untils.func import get_data, last_executed, format_data

def test_get_data(test_filename):
    assert get_data(test_filename) == [{"id": 816266176, "state": "CANCELED", "date": "2018-06-24T00:46:32.422648", "operationAmount": {"amount": "60030.73", "currency": {"name": "USD", "code": "USD"}}, "description": "Перевод организации", "from": "МИР 6381702861749111", "to": "Счет 27804394774631586026"},]

def test_last_executed(test_data):
    assert last_executed(test_data) == [{"id": 441945886, "state": "EXECUTED", "date": "2019-08-26T10:50:58.294041", "operationAmount": {"amount": "31957.58", "currency": { "name": "руб.", "code": "RUB"}}, "description": "Перевод организации", "from": "Maestro 1596837868705199", "to": "Счет 64686473678894779589"}, {"id": 41428829, "state": "EXECUTED", "date": "2019-07-03T18:35:29.512364", "operationAmount": {"amount": "8221.37", "currency": {"name": "USD", "code": "USD"}}, "description": "Перевод организации", "from": "MasterCard 7158300734726758", "to": "Счет 35383033474447895560"}, {"id": 939719570, "state": "EXECUTED", "date": "2018-06-30T02:08:58.425572", "operationAmount": {"amount": "9824.07", "currency": {"name": "USD", "code": "USD"}}, "description": "Перевод организации", "from": "Счет 75106830613657916952", "to": "Счет 11776614605963066702"}, {"id": 587085106, "state": "EXECUTED", "date": "2018-03-23T10:45:06.972075", "operationAmount": {"amount": "48223.05", "currency": {"name": "руб.", "code": "RUB"}}, "description": "Открытие вклада", "to": "Счет 41421565395219882431"}, {"id": 142264268, "state": "EXECUTED", "date": "2019-04-04T23:20:05.206878", "operationAmount": {"amount": "79114.93", "currency": {"name": "USD", "code": "USD"}}, "description": "Перевод со счета на счет", "from": "Счет 19708645243227258542", "to": "Счет 75651667383060284188"},]

def test_format_data():
    assert format_data([{"id": 214024827, "date": "2018-12-20T16:43:26.929246", "operationAmount": {"amount": "70946.18", "currency": {"name": "USD", "code": "USD"}}, "description": "Перевод организации", "from": "Счет 10848359769870775355", "to": "Счет 21969751544412966366"},]) == ['\n20.12.2018 Перевод организации\nСчет **5355 -> Счет **6366\n70946.18 USD\n']